import React, { useState } from "react";
import "./Forecast.css";
import WeatherIcon from "./WeatherIcon";
import axios from "axios";


export default function Forecast(props) {
let [loaded, setLoaded] = useState(false);
let [forecast, setForecast] = useState(null);

function handleResponse(response){
setForecast(response.data.daily);
setLoaded(true);
}



if(loaded){
  return <div className="Forecast">
    <div class="weather-forecast" id="forecast">
    <div class="weather-forecast-day">
          <div class="weather-forecast-date">Sun</div>
          <div class="weather-forecast-icon">
          <WeatherIcon code="few-clouds-night" size={60} />
          </div>
          <div class="weather-forecast-temperatures">
            <div class="weather-forecast-temperature">
              <strong>{forecast[1]}</strong>
            </div>
            <div class="weather-forecast-temperature">30ยบ</div>
          </div>
        </div>

    </div>
  </div>;}
  else{

  let key = "do37btb04e66032f8eb1ab0493255777";
  let apiUrl = `https://api.shecodes.io/weather/v1/forecast?query=${props.city}&key=${key}&units=metric`;

  axios.get(apiUrl).then(handleResponse);
    return null;
  }
}
