import React from "react";

export default function FormatDate(props) {
    let days = [
        "Sunday",
        "Monday",
        "Tuesday",
        "Wednesday",
        "Thursday",
        "Friday",
        "Saturday",
      ];
      let day = days[props.date.getDay()];
      function minutesFormat(minutes) {
        if (minutes < 10) {
          return `0${minutes}`;
        } else {
          return minutes;
        }
      }
      let minutes = minutesFormat(props.date.getMinutes());

  return <div>{day} {props.date.getHours()}:{minutes}</div>;
}
