import React, { useState } from "react";
import "./CurrentTemp.css";


export default function CurrentTemp(props) {
  const [unit,setUnit]=useState('celsius');
  const [temp,setTemp]=useState(props.valueCelsius);
  function convertFahrenheit(event){
    event.preventDefault();
    setUnit("fahrenheit");
    setTemp(Math.round((props.valueCelsius*9/5)+32));
  }
  function convertCelsius(event){
    event.preventDefault();
    setUnit("celsius");
    setTemp(props.valueCelsius);
  }

if (unit==='celsius'){
  return <span className="CurrentTemp">
    <span className="current-temperature">
        <span className="current-temperature-value">{props.valueCelsius}</span>
        <span className="current-temperature-unit">째C|째<a href="/"
        onClick={convertFahrenheit}>F</a></span>
      </span>
  </span>;}
  else {
    return <span className="CurrentTemp">
    <span className="current-temperature">
        <span className="current-temperature-value">{temp}</span>
        <span className="current-temperature-unit">째F|째<a href="/"
        onClick={convertCelsius}>C</a></span>
      </span>
  </span>;;
  }
}
